import { Component } from '@angular/core';

@Component({
  selector: 'app-multi-wonk',
  templateUrl: './multi-wonk.component.html',
  styleUrls: ['./multi-wonk.component.css']
})
export class MultiWonkComponent {

  allowNewWonk = false;

  constructor() {
    setTimeout(() => {
      this.allowNewWonk = true;
    },2000)
  }

}
